import React from "react";
import cx from "classnames";
import "./app.scss"
import Collapse from "@kunukn/react-collapse";
import dummyProducts from ".../services/dummy.js"

class Details extends React.Component {
  state = {
    isOpen1: false,
    spy3: {}
  };

  render(description) {
    return (
      <div className="app">
        <button
          className={cx("app__toggle", {
            "app__toggle--active": this.state.isOpen1
          })}
          onClick={() => this.toggle(1)}
        >
          <span className="app__toggle-text">DÃ©tails</span>
          <div className="rotate90">
            <svg
              className={cx("icon", { "icon--expanded": this.state.isOpen1 })}
              viewBox="6 0 12 24"
            >
              <polygon points="8 0 6 1.8 14.4 12 6 22.2 8 24 18 12" />
            </svg>
          </div>
        </button>
        <Collapse
          isOpen={this.state.isOpen1}
          className={
            "app__collapse app__collapse--gradient " +
            (this.state.isOpen1 ? "app__collapse--active" : "")
          }
        >
          <div className="app__content">
            {description}
            <img
              className="image"
              alt="random"
              src="https://source.unsplash.com/user/erondu/500x200"
            />
            <button onClick={() => this.toggle(1)} className="app__button">
              close
            </button>
          </div>
        </Collapse>
      </div>
    );
  }

  toggle = index => {
    let collapse = "isOpen" + index;

    this.setState(prevState => ({ [collapse]: !prevState[collapse] }));
  };
}

export default Details;
