import Layout from "../../components/Layout";
import React from "react";
import "./loginStyle.scss";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Signin from "./signin";
import About from '../pages/About';


const Login = () => {
  return (
    <Layout title="Connexion" description="This is the Cart page">
      <i class="fa fa-user-circle d-block mx-auto" aria-hidden="true"></i>
      <h3 className="d-block mx-auto m-text">Connection</h3>
      <input
        className=" d-block m-input mx-auto"
        placeholder="E-mail"
        type="e-mail"
      />
      <input
        className=" d-block m-input mx-auto"
        placeholder="*******"
        type="password"
      />
      <div className="m-div">
        <button type="button" className="btn m-button btn-outline-primary">
          Connexion
        </button>
      </div>
      <Router>
        <Switch>
          <Route path="./signin" component={Signin} />
          <Route path="/about" component={About} />                                    
        </Switch>
      </Router>
      <p className="text-center">
        Vous n'avez pas encore de compte ? <br />
        <a href="www.google.com" className="signin">
          {" "}
          Inscrivez-vous gratuitement
        </a>
      </p>
    </Layout>
  );
};
export default Login;
